html, body {
  background-color: #0A2647;
  color: rgba(255, 255, 255, 0.9);
  font-family: 'Poppins', sans-serif;
  text-align: center;
  padding-top: 3em;
  margin: 0;
}

.container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  gap: 2rem;
  min-height: 100vh;
}

.shine, .chrome {
  font-size: 3.8em;
  font-weight: 700;
  margin: 0;
  background: #222 -webkit-gradient(linear, left top, right top, from(#222), to(#222), color-stop(0.5, #fff)) 0 0 no-repeat;
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  color: rgba(255, 255, 255, 0.9);
  text-shadow: 0 0 15px rgba(255, 255, 255, 0.15);
  letter-spacing: 2px;
  animation: fadeIn 2s ease-in-out;
}

.shine {
  -webkit-background-size: 150px;
  -webkit-animation: shine 8s infinite linear;
}

.chrome {
  background-image: -webkit-linear-gradient(-40deg, transparent 0%, transparent 40%, #fff 50%, transparent 60%, transparent 100%);
  -webkit-background-size: 200px;
  -webkit-animation: shine 8s infinite linear;
}

.button {
  background: linear-gradient(135deg, #205295, #2C74B3);
  border: none;
  border-radius: 10px;
  padding: 0.8rem 2rem;
  color: #fff;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(44, 116, 179, 0.4);
}

.button:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 20px rgba(44, 116, 179, 0.6);
}

@-webkit-keyframes shine {
  0%, 10% {
    background-position: -1000px;
  }
  30% {
    background-position: top left;
  }
  70% {
    background-position: top right;
  }
  100% {
    background-position: 1000px;
  }
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

:root {
  --accent-color: #03e9f4;
  --white-color: #fff;
  --box-shadow: 0 0 5px #03e9f4, 0 0 25px #03e9f4, 0 0 50px #03e9f4, 0 0 100px #03e9f4;
  --body-font: 'Montserrat', sans-serif;
}

.neon-border-button {
  position: relative;
  display: inline-block;
  padding: 20px 30px;
  color: var(--accent-color);
  text-transform: uppercase;
  overflow: hidden;
  letter-spacing: 4px;
  transition: 0.5s;
  font-family: var(--body-font);
}

.neon-border-button span {
  position: absolute;
  display: block;
}

.neon-border-button span:nth-child(1) {
  top: 0;
  left: -100%;
  width: 100%;
  height: 2px;
  background: linear-gradient(90deg, transparent, var(--accent-color));
  animation: btn-anim1 1s linear infinite;
}

@keyframes btn-anim1 {
  0% { left: -100%; }
  50%, 100% { left: 100%; }
}

.neon-border-button span:nth-child(2) {
  top: -100%;
  right: 0;
  width: 2px;
  height: 100%;
  background: linear-gradient(180deg, transparent, var(--accent-color));
  animation: btn-anim2 1s linear infinite;
  animation-delay: 0.25s;
}

@keyframes btn-anim2 {
  0% { top: -100%; }
  50%, 100% { top: 100%; }
}

.neon-border-button span:nth-child(3) {
  bottom: 0;
  right: -100%;
  width: 100%;
  height: 2px;
  background: linear-gradient(270deg, transparent, var(--accent-color));
  animation: btn-anim3 1s linear infinite;
  animation-delay: 0.5s;
}

@keyframes btn-anim3 {
  0% { right: -100%; }
  50%, 100% { right: 100%; }
}

.neon-border-button span:nth-child(4) {
