css:
@keyframes bounce {
  0%, 20%, 53%, 80%, 100% { transform: translate3d(0,0,0); }
  40%, 43% { transform: translate3d(0,-30px,0); }
  70% { transform: translate3d(0,-15px,0); }
  90% { transform: translate3d(0,-4px,0); }
}
.bounce {
  animation: bounce 2s ease infinite;
  color: #fff;
}

.flip-card {
  perspective: 1000px;
  width: 200px;
  height: 200px;
}
.flip-card-inner {
  position: relative;
  width: 100%;
  height: 100%;
  text-align: center;
  transition: transform 0.6s;
  transform-style: preserve-3d;
}
.flip-card:hover .flip-card-inner {
  transform: rotateY(180deg);
}
.flip-card-front, .flip-card-back {
  position: absolute;
  width: 100%;
  height: 100%;
  backface-visibility: hidden;
  border-radius: 8px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: bold;
}
.flip-card-front {
  background: linear-gradient(45deg, #667eea, #764ba2);
  color: white;
}
.flip-card-back {
  background: linear-gradient(45deg, #f093fb, #f5576c);
  color: white;
  transform: rotateY(180deg);
}

.typewriter {
  overflow: hidden;
  color: #fff;
  border-right: 3px solid #ddd;
  white-space: nowrap;
  margin: 0 auto;
  animation: 
    typing 3.5s steps(40, end),
    blink-caret 0.75s step-end infinite;
}
@keyframes typing {
  from { width: 0 }
  to { width: 100% }
}
@keyframes blink-caret {
  from, to { border-color: transparent }
  50% { border-color: #333; }
}

.staggered-item {
  opacity: 0;
  color: #fff;
  transform: translateY(20px);
  animation: staggeredFade 0.5s ease-out forwards;
}
@keyframes staggeredFade {
  to { 
    opacity: 1; 
    transform: translateY(0); 
  }
}

.particle-burst {
  position: relative;
  cursor: pointer;
  padding: 20px;
  background: #667eea;
  color: white;
  border-radius: 8px;
  transition: transform 0.3s ease;
}
.particle-burst::before {
  content: '';
  position: absolute;
  top: 0; left: 0; right: 0; bottom: 0;
  background: radial-gradient(circle, transparent 30%, rgba(255,255,255,0.8) 70%);
  border-radius: 8px;
  opacity: 0;
  transition: opacity 0.3s ease;
}
.particle-burst.active::before {
  animation: particleBurst 0.6s ease-out;
}
@keyframes particleBurst {
  0% { 
    transform: scale(1);
    opacity: 1;
  }
  50% {
    transform: scale(1.2);
    opacity: 0.7;
  }
  100% {
    transform: scale(1);
    opacity: 0;
  }
}

js:
// Add click event to trigger particle effect
document.querySelector('.particle-burst').addEventListener('click', function() {
  this.classList.toggle('active');
});

css:
.magnetic-button {
  position: relative;
  padding: 12px 24px;
  background: linear-gradient(45deg, #667eea, #764ba2);
  color: white;
  border: none;
  border-radius: 8px;
  cursor: pointer;
  overflow: hidden;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}
.magnetic-button::before {
  content: '';
  position: absolute;
  top: var(--y, 50%);
  left: var(--x, 50%);
  width: 0;
  height: 0;
  background: radial-gradient(circle, rgba(255,255,255,0.3) 0%, transparent 70%);
  transition: width 0.3s ease, height 0.3s ease;
  transform: translate(-50%, -50%);
}
.magnetic-button:hover::before {
  width: 200px;
  height: 200px;
}
.magnetic-button:hover {
  transform: scale(1.05);
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
}

js:
document.querySelector('.magnetic-button').addEventListener('mousemove', function(e) {
  const rect = this.getBoundingClientRect();
  const x = e.clientX - rect.left;
  const y = e.clientY - rect.top;
  this.style.setProperty('--x', x + 'px');
  this.style.setProperty('--y', y + 'px');
});

css:
.hover-underline {
  position: relative;
  text-decoration: none;
  color: #fff;
  font-size: 1.2rem;
  padding: 10px 0;
}
.hover-underline::after {
  content: '';
  position: absolute;
  bottom: 0;
  left: 0;
  width: 0;
  height: 2px;
  background: linear-gradient(45deg, #667eea, #764ba2);
  transition: width 0.3s ease;
}
.hover-underline:hover::after {
  width: 100%;
}

.gradient-border {
  padding: 20px;
  background: white;
  border: 4px solid transparent;
  background: 
    linear-gradient(white, white) padding-box,
    linear-gradient(45deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4) border-box;
  border-radius: 8px;
  position: relative;
}
