  transition: width 0.3s ease, height 0.3s ease;
  transform: translate(-50%, -50%);
}
.magnetic-button:hover::before {
  width: 200px;
  height: 200px;
}
.magnetic-button:hover {
  transform: scale(1.05);
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
}

