.dark-rider-card:hover .character {
  opacity: 1;
  transform: translate3d(0%, -30%, 100px);
}

.dark-rider-card .title {
  width: 100%;
  transition: transform 0.5s;
  position: absolute;
  bottom: 20px;
  z-index: 3;
}

.dark-rider-card:hover .title {
